def generate_svg(icon_name: str) -> str:
    icon_size = 32
    padding = 12  # space between icon and rect
    margin = 16  # space between SVG edge and rect

    rect_size = icon_size + 2 * padding
    svg_size = rect_size + 2 * margin
    scale_factor = icon_size / 24  # original icon is 24x24

    # Conditional animation for website
    rotation = ""
    if icon_name == "website":
        rotation = """
            <animateTransform attributeName="transform"
                              attributeType="XML"
                              type="rotate"
                              from="0 12 12"
                              to="360 12 12"
                              dur="4s"
                              repeatCount="indefinite"/>
        """

    return f"""
    <svg viewBox="0 0 {svg_size} {svg_size}" xmlns="http://www.w3.org/2000/svg" width="{svg_size}" height="{svg_size}">
        <style>
            :root {{
                --icon-color: #59636e;
                --button-border-color: #d1d9e0;
                --button-background-color: #f6f8fa;
            }}
            @media (prefers-color-scheme: dark) {{
                :root {{
                    --icon-color: #9198a1;
                    --button-border-color: #3d444d;
                    --button-background-color: #2a313c;
                }}
            }}

            path.icon {{
                fill: var(--icon-color);
            }}

            rect.button {{
                fill: var(--button-background-color);
                stroke: var(--button-border-color);
                stroke-width: 1px;
                rx: 6px;
                ry: 6px;
            }}
        </style>

        <!-- Button background -->
        <rect class="button" x="{margin}" y="{margin}" width="{rect_size}" height="{rect_size}" />

        <!-- Scaled icon centered inside rect -->
        <g transform="translate({margin + padding}, {margin + padding}) scale({scale_factor})">
            <path class="icon" d="{ICONS[icon_name]}">
                {rotation}
            </path>
        </g>
    </svg>
    """

def save_svg(filename: str, content: str):
    with open(filename, "w") as f:
        f.write(content)
    print(f"Saved SVG to {filename}")

if __name__ == "__main__":
    for icon_name in ICONS:
        svg = generate_svg(icon_name)
        save_svg(f"{icon_name}.svg", svg)
